import React from "react";
import { Formik, Form } from "formik";
import * as Yup from "yup";
import FormikControl from "./FormikControl";

const FormikContainer = () => {

  const dropdwonOption = [
    { key: "select an option", value: "" },
    { key: "option 1", value: "option1" },
    { key: "option 2", value: "option2" },
    { key: "option 3", value: "option3" },
  ];

  const radioOption = [
    { key: "option 1", value: "roption1" },
    { key: "option 2", value: "roption2" },
    { key: "option 3", value: "roption3" },
  ];

  const initialValues = {
    email: "",
    description: "",
    selectOption: " ",
    radioButton:"",
  };

  const validationSchema = Yup.object({
    email: Yup.string()
      .required(<div className="text-red-600">Required</div>)
      .email("Invalid Email Format"),
    description: Yup.string().required(
      <div className="text-red-600">Required</div>
    ),
    selectOption: Yup.string().required(
      <div className="text-red-600">Required</div>
    ),
    radioButton : Yup.string().required(
      <div className="text-red-600">Required</div>
    ),
  });

  const onSubmit = (values) => {
    console.log(values);
    alert(JSON.stringify(values, null, 2));
  };
  return (
    <div>
      <Formik
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={onSubmit}
      >
        {(formik) => (
          <Form className="flex flex-col w-[500px] h-[600px] ml-10 mt-5">
            <FormikControl
              control="input"
              type="email"
              label="Email"
              name="email"
            />

            <FormikControl
              control="textarea"
              label="Description"
              name="description"
            />

            <FormikControl
              control="select"
              label="Select The Topic"
              name=" selectOption:"
              options={dropdwonOption}
            />

            <FormikControl
              control="radio"
              label="Radio Topic"
              name=" radioOption:"
              options={radioOption}
            />

            <button type="submit">Submit</button>
          </Form>
        )}
      </Formik>
    </div>
  );
};

export default FormikContainer;
