import Data from "./Data";
import React, { useState } from "react";
import List from "./List";

const Birthday = () => {
  const [people, SetPeople] = useState(Data);
  return (
    <div className="bg-pink-500 w-full  h-screen flex justify-center items-center">
      <div className="bg-white w-[400px] h-[600px] rounded-md shadow-md shadow-gray-900">
        <h2 className="text-2xl font-bold tracking-wider font-mono ml-12 mt-5 opacity-75">
          {people.length} Birthday today
        </h2>
        <List people={people} />
        <div>
          <button
            onClick={() => SetPeople([])}
            className="bg-pink-500 ml-12 px-32 py-1 mt-8 rounded-md shadow-sm text-white font-bold shadow-gray-900 "
          >
            Clear All
          </button>
          <button
            onClick={() => SetPeople(Data)}
            className="bg-pink-500 ml-12 px-10 py-1 mt-2 rounded-md shadow-sm text-white font-bold shadow-gray-900 "
          >
            Reset
          </button>
        </div>
      </div>
    </div>
  );
};

export default Birthday;
