import React, { useState } from "react";
const Tour = ({id,image,info,price,name}) => {
  const [readMore,setReadMore]=useState(false)

  return <article className="grid justify-items-center mt-16 border justify-center w-full md:w-1/3 content-center md:ml-[450px] rounded-lg shadow-lg shadow-gray-300 ">
    <img className="w-[500px] h-64 rounded-t-lg " src={image} alt={name} />
    <footer className="overflow-hidden ">
        <div className="flex justify-between mt-5">
        <h4 className="font-bold">{name}</h4>
        <h4 className="text-blue-500 font-bold">â‚¹ {price} </h4>
        </div>
        <p className="overflow-hidden mt-3  opacity-75  w-[400px]" >{readMore? info:`${info.substring(0,200)}...`}
        <button onClick={()=>setReadMore(!readMore)} className='text-blue-700'>{ readMore?'show Less':'read more'}</button></p>
        <button className="ml-14 px-20 py-1 border-2 border-red-700 rounded-lg mt-4 mb-5 text-red-600 shadow-lg shadow-gray-300 font-bold">Not Intrested</button>
    </footer>

  </article>
}
export default Tour;
